import "./style.css";

const counter = document.getElementById('counter');
const message = document.getElementById('message');
const progress = document.getElementById('progress');
const progressBar = document.getElementById('progress-bar');
let interval;
let tabataCounter = 20;
let restCounter = 11;
let isRoundActive = false;
let stage = 1;
let isPause = false;
let isTabataActive = false;
var tic = new Audio("data:audio/wav;base64,");
document.querySelector('.container').addEventListener('click', toggleStartPause);

function startNewTabata() {
  isTabataActive = true;
  isRoundActive = true;
  notification('Go', `Round ${stage}. Go.`);
  progress.style.display = 'block';
  interval = setInterval(() => {
    round();
    rest();
  }, 1000);
};

function toggleStartPause() {
  if (!isTabataActive) {
    startNewTabata();
  } else {
    isPause = !isPause;
    counter.innerHTML = '';
    if (isPause) {
      notification('Pause. Press anywhere to continue.', 'Pausing tabata.');
    } else {
      notification('', 'Resuming tabata.');
    }
  }
}

function round() {
  if (isRoundActive && isTabataActive && !isPause) {
    tic.play();
    message.innerHTML = 'Round ' + stage;
    if ([5, 4, 3, 2, 1].includes(tabataCounter)) {
      notification(undefined, tabataCounter);
    }
    counter.innerHTML = tabataCounter;
    if (tabataCounter === 0) {
      setupRest();
    }
    inceraseProgressBar();
    if (tabataCounter > 0) {
      tabataCounter -= 1;
    }
  }
}

function rest() {
  if (!isRoundActive && !isPause && isTabataActive) {
    message.innerHTML = `Rest`;
    counter.innerHTML = restCounter;
    if ([3, 2, 1].includes(restCounter)) {
      notification(undefined, restCounter);
    }
    if (restCounter === 0) {
      setupNewRound();
    }
    if (restCounter > 0) {
      restCounter -= 1;
    }
  }
}

function inceraseProgressBar() {
  progressBar.style.width = `${Math.round(100 / 8) * stage}%`;
  progressBar.innerHTML = stage;
}

function setupRest() {
  notification(undefined, 'rest!');
  restCounter = 10;
  isRoundActive = false;
  counter.innerHTML = restCounter;
}

function setupNewRound() {
  counter.innerHTML = '';
  tabataCounter = 20;
  stage += 1;
  if (stage > 8) {
    notification('Tabata over. Click anywhere to start new tabata', 'Congratulations! You finished.');
    isTabataActive = false;
    clearInterval(interval);
    tabataCounter = 21;
    restCounter = 11;
    isRoundActive = false;
    stage = 1;
    isPause = false;
    isTabataActive = false;
  } else {
    message.innerHTML = 'Round ' + stage;
    isRoundActive = true;
    notification('', `Round ${stage}. Go.`);
  }
}

function notification(text, voice) {
  if (text) {
    message.innerHTML = text;
  }
  if (voice) {
    window.speechSynthesis.speak(new SpeechSynthesisUtterance(voice));
  }
}